(function() {
  var App, Pry, pry;

  App = require('./pry/app');

  Pry = (function() {
    function Pry(arg) {
      this.isStandAlone = (arg != null ? arg : {}).isStandAlone;
      this.it = "(" + (this._pry.toString()) + ").call(this)";
    }

    Pry.prototype._pry = function() {
      var _;
      _ = null;
      return pry.open((function(input) {
        return _ = eval(input);
      }).bind(this));
    };

    Pry.prototype.open = function(scope) {
      var app;
      app = new App(scope, this.isStandAlone);
      return app.open();
    };

    return Pry;

  })();

  module.exports = new Pry;

  if (require.main === module) {
    eval((pry = new Pry({
      isStandAlone: true
    })).it);
  }

}).call(this);
